mapper.select:
SELECT ID as id, STUDY_NAME as studyName, STUDY_DESCRIPTION as studyDesc, LICENSE as license, IS_PUBLIC as isPublic, if(IS_PUBLIC = 1, "Yes", "No") as isPublicString, AUTHORS as authors, PUBLICATION as publication, CONTACT as contact, SITE_COLLECTED as siteCollected, CREATED_BY as createdBy, CREATED_DATE as createdDate, CREATED_IP as createdIp FROM tbl_study
WHERE
ID=#{id}
AND
CREATED_BY = #{createdBy}

SELECT ID as id, STUDY_ID as studyId, FILE_NAME as fileName, FILE_FORMAT as fileFormat, FILE_PATH as filePath, FILE_SIZE as fileSize, CONTENT_TYPE as contentType, DATASET_NAME as dataSetName, TASK_TYPE as taskType, if(ENABLED = 1, "Yes", "No") as isEnabledString, DOWNLOAD_COUNT as downloadCount, VERSION as version, CREATED_BY as createdBy, CREATED_DATE as createdDate, CREATED_IP as createdIp FROM tbl_dataset
WHERE
STUDY_ID = #{studyId}
AND
ID = #{dataSetId}

mapper.update:
UPDATE tbl_dataset SET
FILE_NAME = #{fileName}, FILE_FORMAT = #{fileFormat}, FILE_PATH = #{filePath}, FILE_SIZE = #{fileSize}, CONTENT_TYPE = #{contentType}, VERSION = VERSION + 1,
DATASET_NAME = #{dataSetName}, TASK_TYPE = #{taskType}, LAST_MODIFIED_BY = #{lastModifiedBy}, LAST_MODIFIED_DATE = #{lastModifiedDate}, LAST_MODIFIED_IP = #{lastModifiedIp} WHERE ID = #{id}

UPDATE #{tableName}
SET
DOWNLOAD_COUNT = DOWNLOAD_COUNT + 1
WHERE
ID = #{id}
